  return (
    <TracksContext.Provider
      value={{
        search,
        result,
        handleInputChange,
        handleFormSubmit,
      }}
    >
      <Layout>
        <Header />
        <Main>
          <ShowEmployees />
        </Main>
        <Footer />
      </Layout>
    </TracksContext.Provider>
  );
};

export default EmployeeContainer;
